@-moz-document domain("inoreader.com") {
body html {
    height: 100vh!important
}
#all_gad_6125,
#sinner_container,
.ad_title,
.avs_iframe,
.block_article_ad {
    display: none!important
}
.reader_pane_sinner {
    padding-right: 0!important
}
#wraper #reader_pane.reader_pane_view_style_3 .articles_feed_group_heading>.articles_feed_group_heading_title,
#wraper #reader_pane.reader_pane_view_style_4 .articles_feed_group_heading>.articles_feed_group_heading_title {
    display: none;
    font-size: 10pt;
    padding: 0
}
.article_tile {
    margin: 12px .5% 0
}
.reader_pane_view_style_3 .article_title_link {
    position: fixed;
    overflow: hidden;
    font-size: 8pt;
    line-height: 120%!important;
    width: 90%!important;
    height: 3.5vh!important;
    left: 0;
    padding: 20px 20px 5% 20px !important;
    bottom: 20px;
    color: #fff!important;
    background: rgba(0, 0, 0, .0) !important;
    transition: transform 0.3s linear!important;
    -moz-transition: transform 0.3s linear!important;
    -webkit-transition: transform 0.3s linear!important;
    -o-transition: transform 0.3s linear!important;
    -ms-transition: transform 0.3s linear!important;
}
.reader_pane_view_style_3 .ar:hover .article_tile_picture {
    -webkit-filter: brightness(50%);
    -webkit-transition: all 200ms ease-in;
    -webkit-transform: scale(1.1) rotate(2deg);
    -ms-transition: all 200ms ease-in;
    -ms-transform: scale(1.1) rotate(2deg);
    -moz-transition: all 200ms ease-in;
    -moz-transform: scale(1.12) rotate(2deg);
    transition: all 200ms ease-in;
    transform: scale(1.1) rotate(2deg);
}
.article_tile_picture:before {
    display: inline-block;
    content: "";
    width: 100vw !important;
    height: 45vh !important;
    background: -webkit-linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, .0), rgba(0, 0, 0, .0), rgba(0, 0, 0, .9))!important;
    background: -o-linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, .0), rgba(0, 0, 0, .0), rgba(0, 0, 0, .9))!important;
    background: -moz-linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, .0), rgba(0, 0, 0, .0), rgba(0, 0, 0, .9))!important;
    background: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, .0), rgba(0, 0, 0, .0), rgba(0, 0, 0, .9))!important;
}
.article_tile_picture {
    height: 100%;
    width: 100%;
    /*-webkit-transition: all .5s ease;
    -moz-transition: all .5s ease;
    -ms-transition: all .5s ease;
    -o-transition: all .5s ease;
    transition: all .5s ease*/

    -webkit-transition: all 200ms ease-in;
    -webkit-transform: scale(1);
    -ms-transition: all 200ms ease-in;
    -ms-transform: scale(1);
    -moz-transition: all 200ms ease-in;
    -moz-transform: scale(1);
    transition: all 200ms ease-in;
    transform: scale(1);
}
.article_tile_picture:hover {
    filter: brightness(50%);
    -webkit-filter: brightness(50%);
    -webkit-transition: all 200ms ease-in;
    -webkit-transform: scale(1.1) rotate(2deg);
    -ms-transition: all 200ms ease-in;
    -ms-transform: scale(1.1) rotate(2deg);
    -moz-transition: all 200ms ease-in;
    -moz-transform: scale(1.12) rotate(2deg);
    transition: all 200ms ease-in;
    transform: scale(1.1) rotate(2deg);
    /*background-position: 60% 50%;
    transform: translate3d(0, 0, 0) scale(var(--scales))!important;
    -o-transform: translate3d(0, 0, 0) scale(var(--scales))!important;
    -ms-transform: translate3d(0, 0, 0) scale(var(--scales))!important;
    -moz-transform: translate3d(0, 0, 0) scale(var(--scales))!important;
    -webkit-transform: translate3d(0, 0, 0) scale(var(--scales))!important*/
}
